#' Response Optimization
#'
#' 
#' @param data .
#' @param deps .
#' @param indeps .
#' @param modelTerms .
#' @param targetGoals .
#' @param lowerUpperBounds .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$usage} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$globalSolution} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$predResponses} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$individualDes} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$compositeDes} \tab \tab \tab \tab \tab a preformatted \cr
#' }
#'
#' @export
resOptimization <- function(
    data,
    deps,
    indeps,
    modelTerms,
    targetGoals,
    lowerUpperBounds) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("resOptimization requires jmvcore to be installed (restart may be required)")

    if ( ! missing(deps)) deps <- jmvcore::resolveQuo(jmvcore::enquo(deps))
    if ( ! missing(indeps)) indeps <- jmvcore::resolveQuo(jmvcore::enquo(indeps))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(deps), deps, NULL),
            `if`( ! missing(indeps), indeps, NULL))

    if (inherits(modelTerms, "formula")) modelTerms <- jmvcore::decomposeFormula(modelTerms)

    options <- resOptimizationOptions$new(
        deps = deps,
        indeps = indeps,
        modelTerms = modelTerms,
        targetGoals = targetGoals,
        lowerUpperBounds = lowerUpperBounds)

    analysis <- resOptimizationClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

